// Collects the job title directly from the 'message.content.job_titles' field 
// of every single incoming item.

// 1. Collect all job titles into a single array
let titlesArray = $input.all().map(item => {
    // Safely access the job title string from the current item
    // Note: The input items are likely structured like:
    // { "message.content.job_titles": "Chief Technology Officer" }
    return item.json['message.content.job_titles'] || "";
});

// 2. Clean the array (remove any potential empty strings if the field was null)
titlesArray = titlesArray.filter(title => title.trim().length > 0);

// Optional: remove duplicates (still good practice)
titlesArray = [...new Set(titlesArray)];

// --- Context Retrieval ---
// Assuming 'Code5' is the correct node name/ID for the original job data
// NOTE: Make sure the Code5 node is correctly connected or adjust the selector
const originalJobTitle = $('Code5').first().json['jobTitle'];

// Return a single item containing the final array and context
return [{
    json: {
        originalTitle: originalJobTitle,
        titlesArray: titlesArray, // Array of clean job title strings
        // Required for Apify: JSON string of the array
        queriesJsonString: JSON.stringify(titlesArray) 
    }
}];

const processedCandidates = [];
const now = new Date();
const currentYear = now.getFullYear();
const currentMonth = now.getMonth() + 1; // Current month (1-12)

// --- Helper function to format tenure (Xy Ym) ---
const formatTenure = (totalMonths) => {
    if (totalMonths <= 0) return "Less than 1 month";

    const years = Math.floor(totalMonths / 12);
    const months = totalMonths % 12;

    let parts = [];
    if (years > 0) {
        parts.push(`${years}y`);
    }
    if (months > 0) {
        parts.push(`${months}m`);
    }
    return parts.join(' ');
};

// --- Loop through ALL items passed to this Code Node ---
for (const item of $input.all()) {
    const profile = item.json; 
    let tenureInMonths = 0;

    // --- 1. Tenure Calculation (Internal Use Only) ---
    const currentJobData = profile.currentPosition && profile.currentPosition.length > 0 
      ? profile.currentPosition[0] 
      : null;

    if (currentJobData && currentJobData.dateRange && currentJobData.dateRange.start) {
      const startDate = currentJobData.dateRange.start;
      const startYear = startDate.year;
      const startMonth = startDate.month; 

      if (startYear && startMonth) {
        const yearDifferenceInMonths = (currentYear - startYear) * 12;
        const monthDifference = currentMonth - startMonth;

        tenureInMonths = Math.floor(yearDifferenceInMonths + monthDifference);
        if (tenureInMonths < 0) {
          tenureInMonths = 0;
        }
      }
    }

    // --- 2. Flatten Experience History String ---
    const experienceString = profile.experience.map(job => {
      const position = job.position || 'N/A Position';
      const company = job.companyName || 'N/A Company';
      const employmentType = job.employmentType || 'N/A';
      const workplaceType = job.workplaceType || 'N/A';
      const location = job.location || 'N/A Location';
      const startDateText = job.startDate ? job.startDate.text : 'N/A Start Date';
      const endDateText = job.endDate ? (job.endDate.text || 'Present') : 'N/A End Date';

      const skillsList = job.skills && job.skills.length > 0
        ? job.skills.map(skill => skill.name).join(',')
        : 'None';

      return `${position} at ${company} (${employmentType}, ${workplaceType}, ${location}) from ${startDateText} to ${endDateText} | Skills: ${skillsList}`;
    }).join('; '); 

    // --- 3. Flatten Education, Skills, and Certifications ---
    const educationString = profile.education.map(edu => {
      const school = edu.schoolName || 'N/A School';
      const degree = edu.degree || 'N/A Degree';
      const period = edu.period || 'N/A Period';
      return `${degree} in ${edu.fieldOfStudy || 'N/A Field'} from ${school} (${period})`;
    }).join('; ');

    const allSkillsString = profile.skills.map(skill => skill.name).join(', ');
    const certificationsString = profile.certifications.map(cert => cert.title).join(', ');


    // --- 4. NEWLINE CLEANUP FOR 'ABOUT' ---
    const cleanAboutText = profile.about
      ? profile.about.replace(/[\n\r]+/g, ' ').trim()
      : 'N/A';


    // --- 5. Construct the Final Clean Profile Object ---
    const cleanProfile = {
      // A. Identification & Outreach
      ID: profile.id,
      linkedinUrl: profile.linkedinUrl,
      fullName: `${profile.firstName} ${profile.lastName}`,
      primaryEmail: profile.emails && profile.emails.length > 0 ? profile.emails[0].email : 'N/A',

      // B. Current Role & Availability
      current_title: profile.headline,
      Tenure_Formatted: formatTenure(tenureInMonths), // ONLY this field is kept
      current_location: profile.location.parsed.text || profile.location.linkedinText,

      // C. Full History for AI Agent (Flattened Strings)
      candidate_about: cleanAboutText, 
      candidate_skills_list: allSkillsString,
      candidate_experience_history: experienceString,
      candidate_education_summary: educationString,
      candidate_certifications: certificationsString,
      candidate_projects: profile.projects.map(p => p.title).join(' | ')
    };

    processedCandidates.push({ json: cleanProfile });
}

return processedCandidates;

return items.map(item => {
  const isConnection = item.json.is_connection;  // true/false
  const isPending = item.json.is_pending;        // true/false

  let actionType = "";       // What action we want to take
  let actionDetail = "";     // Extra info if needed

  if (!isConnection && !isPending) {
    actionType = "invite";             // Not connected, no pending → send invite
    actionDetail = "Send LinkedIn Invite";
  } else if (isConnection) {
    actionType = "message";            // Already connected → send message
    actionDetail = "Send LinkedIn Message";
  } else if (isPending) {
    actionType = "pending";            // Invite pending → optional message
    actionDetail = "Invite Pending";
  } else {
    actionType = "unknown";            // Catch-all
    actionDetail = "Unknown State";
  }

  // Add the fields to each item
  item.json.actionType = actionType;
  item.json.actionDetail = actionDetail;

  return item;
});

const body = $json.body;
const uniqueJobId = 'JOB-' + Date.now() + Math.random().toString(36).substring(2, 8);

// flatten salaryDetails
return {
  json: {
    jobTitle: body.jobTitle,
    jobCode: body.jobCode,
    experience: body.experience,
    employmentType: body.employmentType,
    jobType: body.jobType,
    officeLocation: body.officeLocation,
    department: body.department,
    industry: body.industry,
    currency: body.salaryDetails?.currency,
    minSalary: body.salaryDetails?.minSalary,
    maxSalary: body.salaryDetails?.maxSalary,
    salaryInterval: body.salaryDetails?.salaryInterval,
    jobDescription: body.jobDescription,
    uniqueJobId: uniqueJobId
  }
};

